import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdateProduct = (props) => {
  const [productData, setProductData] = useState({
    name: "",
    price: 0,
    description: "",
    stock: 0,
    category: "",
    image: null,
  });

  const [categories, setCategories] = useState([]);

  const productId = props.match.params.id; // Assuming the product ID is passed through route params

  useEffect(() => {
    // Fetch product details
    axios
      .get(`http://127.0.0.1:8000/api/electronics/${productId}/`)
      .then((response) => {
        const productDetails = response.data;
        setProductData({
          name: productDetails.name,
          price: productDetails.price,
          description: productDetails.description,
          stock: productDetails.stock,
          category: productDetails.category, // Assuming category is directly set as an ID
          image: null, // You can handle image update using a separate endpoint
        });
      });

    // Fetch available categories
    axios.get("http://127.0.0.1:8000/api/categories/").then((response) => {
      setCategories(response.data);
    });
  }, [productId]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Put the updated product data to the server
    axios
      .put(`http://127.0.0.1:8000/api/electronics/${productId}/`, productData)
      .then((response) => {
        console.log("Product updated:", response.data);
        // Redirect to products page after successful update
        window.location.href = "/view_products";
      })
      .catch((error) => {
        console.error("Error updating product:", error);
      });
  };

  return (
    <div className="container mt-4 text-center">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            value={productData.name}
            onChange={(e) =>
              setProductData({ ...productData, name: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input
            type="number"
            className="form-control"
            value={productData.price}
            onChange={(e) =>
              setProductData({ ...productData, price: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Description:</label>
          <textarea
            className="form-control"
            value={productData.description}
            onChange={(e) =>
              setProductData({ ...productData, description: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Stock:</label>
          <input
            type="number"
            className="form-control"
            value={productData.stock}
            onChange={(e) =>
              setProductData({ ...productData, stock: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Category:</label>
          <select
            className="form-control"
            value={productData.category}
            onChange={(e) =>
              setProductData({ ...productData, category: e.target.value })
            }
            required
          >
            <option value="" disabled>
              Select a category
            </option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-primary">
          Update
        </button>
      </form>
    </div>
  );
};

export default UpdateProduct;
