import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdateProduct = () => {
  const { id } = useParams();
  const [productData, setProductData] = useState({
    name: "",
    price: 0,
    description: "",
    stock: 0,
    category: "", // Category ID will be stored here
    image: null,
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch the product details using the ID
    axios
      .get(`http://127.0.0.1:8000/api/electronics/${id}/`)
      .then((response) => {
        const product = response.data;
        setProductData({
          name: product.name,
          price: product.price,
          description: product.description,
          stock: product.stock,
          category: product.category.id, // Assuming category is an object with an 'id' field
          image: null, // You can update image logic if needed
        });
      });

    // Fetch available categories for the combobox
    axios.get("http://127.0.0.1:8000/api/categories/").then((response) => {
      setCategories(response.data);
    });
  }, [id]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setProductData({ ...productData, image: file });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", productData.name);
    formData.append("price", productData.price);
    formData.append("description", productData.description);
    formData.append("stock", productData.stock);
    formData.append("category", productData.category);
    formData.append("image", productData.image);

    // Remove the category and image fields from the form data if they're not changed
    if (productData.category === productData.originalCategory) {
      formData.delete("category");
    }
    if (!productData.image) {
      formData.delete("image");
    }

    // Update the product data on the server
    axios
      .put(`http://127.0.0.1:8000/api/electronics/${id}/`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((response) => {
        console.log("Product updated:", response.data);
        // Redirect to products page after successful update
        window.location.href = "/view_products";
      })
      .catch((error) => {
        console.error("Error updating product:", error);
      });
  };

  return (
    <div className="container mt-4 text-center">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        {/* ...other form fields */}

        <div className="form-group">
          <label>Category:</label>
          <select
            className="form-control"
            value={productData.category}
            onChange={(e) =>
              setProductData({ ...productData, category: e.target.value })
            }
            required
          >
            <option value="" disabled>
              Select a category
            </option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Image:</label>
          <br />
          <input
            type="file"
            className="form-control"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>
        {/* Add input elements for image update if needed */}
        <button type="submit" className="btn btn-primary">
          Update
        </button>
      </form>
    </div>
  );
};

export default UpdateProduct;
