import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdateProduct = () => {
  const { productId } = useParams();
  const [productData, setProductData] = useState({
    name: "",
    price: 0,
    description: "",
    stock: 0,
    category: "", // You may need to set up category selection
    image: null, // You can handle image update using state and input
  });

  useEffect(() => {
    // Fetch product data by ID
    axios
      .get(`http://127.0.0.1:8000/api/electronics/${productId}`)
      .then((response) => {

        setProductData(response.data);
      });
  }, [productId]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Put the updated product data to the server
    axios
      .put(`http://127.0.0.1:8000/api/electronics/${productId}`, productData)
      .then((response) => {
        console.log("Product updated:", response.data);
        // Redirect to products page after successful update
        window.location.href = "/view_products";
      })
      .catch((error) => {
        console.error("Error updating product:", error);
      });
  };

  return (
    <div className="container mt-4">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            value={productData.name}
            onChange={(e) =>
              setProductData({ ...productData, name: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input
            type="number"
            className="form-control"
            value={productData.price}
            onChange={(e) =>
              setProductData({ ...productData, price: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Description:</label>
          <textarea
            className="form-control"
            value={productData.description}
            onChange={(e) =>
              setProductData({ ...productData, description: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Stock:</label>
          <input
            type="number"
            className="form-control"
            value={productData.stock}
            onChange={(e) =>
              setProductData({ ...productData, stock: e.target.value })
            }
            required
          />
        </div>
        {/* Add input elements for category selection and image update */}
        <button type="submit" className="btn btn-primary">
          Update
        </button>
      </form>
    </div>
  );
};

export default UpdateProduct;
