import React, { useState } from "react";
import axios from "axios";

const CreateProduct = () => {
  const [productData, setProductData] = useState({
    name: "",
    price: 0,
    description: "",
    stock: 0,
    category: "", // You may need to set up category selection
    image: null, // You can handle image upload using state and input
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Post the product data to the server
    axios
      .post("http://127.0.0.1:8000/api/products/create", productData)
      .then((response) => {
        console.log("Product created:", response.data);
        // Redirect to products page after successful creation
        window.location.href = "/products";
      })
      .catch((error) => {
        console.error("Error creating product:", error);
      });
  };

  return (
    <div className="container mt-4">
      <h2>Create Product</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields for product data */}
        {/* Use appropriate input elements for each field */}
        <button type="submit" className="btn btn-primary">
          Create
        </button>
      </form>
    </div>
  );
};

export default CreateProduct;
