import React, { useState } from "react";
import axios from "axios";

const CreateProduct = () => {
  const [productData, setProductData] = useState({
    name: "",
    price: 0,
    description: "",
    stock: 0,
    category: "", // You may need to set up category selection
    image: null, // You can handle image upload using state and input
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Post the product data to the server
    axios
      .post("http://127.0.0.1:8000/api/electronics/create", productData)
      .then((response) => {
        console.log("Product created:", response.data);
        // Redirect to products page after successful creation
        window.location.href = "/view_products";
      })
      .catch((error) => {
        console.error("Error creating product:", error);
      });
  };

  return (
    <div className="container mt-4">
      <h2>Create Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            value={productData.name}
            onChange={(e) =>
              setProductData({ ...productData, name: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input
            type="number"
            className="form-control"
            value={productData.price}
            onChange={(e) =>
              setProductData({ ...productData, price: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Description:</label>
          <textarea
            className="form-control"
            value={productData.description}
            onChange={(e) =>
              setProductData({ ...productData, description: e.target.value })
            }
            required
          />
        </div>
        <div className="form-group">
          <label>Stock:</label>
          <input
            type="number"
            className="form-control"
            value={productData.stock}
            onChange={(e) =>
              setProductData({ ...productData, stock: e.target.value })
            }
            required
          />
        </div>
        {/* Add input elements for category selection and image upload */}
        <button type="submit" className="btn btn-primary">
          Create
        </button>
      </form>
    </div>
  );
};

export default CreateProduct;
