import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import { useParams } from "react-router-dom"; // Import useParams to get the category ID
import useProducts from "../hooks/useProducts";

const ProductsByCategory = () => {
  const { categoryId } = useParams(); // Get the category ID from the URL parameter
  const [categoryName, setCategoryName] = useState(""); // State to hold the category name
  const products = useProducts();

  // Fetch the category name based on the categoryId
  useEffect(() => {
    fetch(`http://127.0.0.1:8000/api/categories/${categoryId}/`)
      .then((response) => response.json())
      .then((data) => setCategoryName(data.name))
      .catch((error) => console.error("Error fetching category name:", error));
  }, [categoryId]);

  // Filter products based on the categoryId
  const categoryProducts = products.filter(
    (product) => product.category === parseInt(categoryId)
  );

  return (
    <div className="container">
      <h2 className="text-center mt-5">Products in Category: {categoryName}</h2>
      <div className="row mt-3">
        {categoryProducts.map((product, index) => (
          <div key={index} className="col-lg-3 col-md-4 col-sm-6 mb-4">
            <div className="card position-relative">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top img-fluid"
                style={{ width: "100%", height: "200px" }}
              />
              <div
                className="stock-badge position-absolute"
                style={{
                  right: "10px",
                  top: "10px",
                  padding: "5px",
                  background: "yellow",
                }}
              >
                {product.stock}
              </div>
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">${product.price}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsByCategory;
