import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import useProducts from "../hooks/useProducts";

const SingleProduct = () => {
  const { productId } = useParams();
  const parsedProductId = parseInt(productId); // Convert to a number
  const product = useProducts().find(
    (product) => product.id === parsedProductId
  );

  const [selectedImage, setSelectedImage] = useState(product.image);

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleThumbnailClick = (thumbnailImage) => {
    setSelectedImage(thumbnailImage);
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-6 order-md-2">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">Price: ${product.price}</p>
              <p className="card-text">Stock: {product.stock}</p>
              <p className="card-text">Description: {product.description}</p>
            </div>
          </div>
        </div>
        <div className="col-md-6 order-md-1">
          <div className="card">
            <img
              src={selectedImage}
              alt={product.name}
              className="card-img-top"
              style={{ width: "100%", height: "auto" }}
            />
            <div className="d-flex justify-content-center mt-3">
              {product.thumbnailImages.map((thumbnail, index) => (
                <img
                  key={index}
                  src={thumbnail}
                  alt={`Thumbnail ${index}`}
                  className="img-thumbnail mx-2"
                  style={{ cursor: "pointer" }}
                  onClick={() => handleThumbnailClick(thumbnail)}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
